mapper.select:
select id
from user
where email = #{email,jdbcType=VARCHAR}

mapper.update:
update user
set nickname    = #{nickname,jdbcType=VARCHAR},
    email       = #{email,jdbcType=VARCHAR},
    status      = #{status,jdbcType=TINYINT},
    update_time = #{updateTime,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}
